import React from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import RaisedButton from 'material-ui/RaisedButton';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: 600,
  },
}));

export default function GroupedSelect() {
  const classes = useStyles();

  const categorySelectEl = document.querySelector("#category_select");
  const typeSelectEl = document.querySelector("#type_select");
  
  categorySelectEl.addEventListener("change", (e) => {
    //renderSelectTypes(typeSelectEl, e.target.value);
  });
  
  function renderSelectTypes(el, selectedCategory) {
    const types = [['Phones', 'Tablets'], ['Couch', 'Refrigerator', 'Vacuum']];
    const options = types[Number(selectedCategory) - 1].map(item => `<option value="${selectedCategory}"">${item}</option>`)
    el.innerHTML = options.join("\n");
  }
  
  const currentlySelected = categorySelectEl.options[categorySelectEl.selectedIndex].value;

  return (
    <div>
    <MuiThemeProvider>
    <React.Fragment> 
            <AppBar 
            title="Era Consulting"
            style={styles.banner}
            id="banner"
            />
            <h2 
            style={styles.subheading}
            >Додај</h2>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Избери Фирма</InputLabel>
                <Select native defaultValue="" id="grouped-native-select">
                    <option aria-label="None" value="" />
                    <option value={1}>Option 1</option>
                    <option value={2}>Option 2</option>
                </Select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Категорија</InputLabel>
                <select id="category_select">
    <option value="1">Electronics</option>
    <option value="2">Appliances</option>
  </select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Под-Категорија</InputLabel>
                <select id="type_select">
    <option value="1">Phones</option>
    <option value="1">Tablets</option>
    <option value="2">Couch</option>
    <option value="2">Refrigerator</option>
    <option value="2">Vacuum</option>
  </select>
            </FormControl>
            <br/>
            <RaisedButton 
                label="Назад"
                primary={false}
                style={styles.button}
            />
            <RaisedButton
                label="Додај"
                id="button-continue" 
                primary={true}
                style={styles.button}
            />
    </React.Fragment>
    </MuiThemeProvider>
    </div>
  );
}

const styles ={
    button: {
        margin: 15,
    },
    banner:{
        backgroundColor: "#3257a8"
    },
    subheading: {
        color: "rgb(136, 136, 136)",
        fontWeight: "600"
    },
}
