import React from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import RaisedButton from 'material-ui/RaisedButton';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: 600,
  },
}));

var childs = {
    Electronics: [
      {
        label: 'Phone',
        value: 1
      },
      {
        label: 'Tablet',
        value: 2
      }
    ],
    Appliances: [
      {
        label: 'Refrigerator',
        value: 1
      },
      {
        label: 'Couch',
        value: 2
      },
      {
        label: 'Vaccum',
        value: 3
      }
    ]
  }
  
  function handleChange () {
    var x = document.getElementById("category_select").value;
    
    var childOptions = childs[x]
    var childSelect = document.getElementById('type_select')
    childSelect.innerHTML =''
    
    childOptions.forEach(function (option) {
      var optionEle = document.createElement('option')
      optionEle.value = option.value
      optionEle.label = option.label
      
      childSelect.appendChild(optionEle)
    })
    
  }
  handleChange()

export default function GroupedSelect() {
  const classes = useStyles();
  return (
    <div>
    <MuiThemeProvider>
    <React.Fragment> 
            <AppBar 
            title="Era Consulting"
            style={styles.banner}
            id="banner"
            />
            <h2 
            style={styles.subheading}
            >Додај</h2>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Избери Фирма</InputLabel>
                <Select native defaultValue="" id="grouped-native-select">
                    <option aria-label="None" value="" />
                    <option value={1}>Option 1</option>
                    <option value={2}>Option 2</option>
                </Select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Категорија</InputLabel>
                <Select native defaultValue="" id="grouped-native-select">
                    <option aria-label="None" value="" />
                    <option value={1}>01 - ОСНОВАЊЕ, ОРГАНИЗАЦИЈА И РАЗВОЈ</option>
                    <option value={2}>02 - УПРАВУВАЊЕ И РАКОВОДЕЊЕ</option>
                    <option value={3}>03 - КАНЦЕЛАРИСКО - АРХИВСКИ, ПРАВНИ И ОПШТИ РАБОТИ</option>
                    <option value={4}>04 - ЧОВЕЧКИ РЕСУРСИ</option>
                    <option value={5}>05 - ФИНАНСИСКО И МАТЕРИЈАЛНО РАБОТЕЊЕ</option>
                    <option value={6}>06 - ОДБРАНА И БЕЗБЕДНОСТ</option>
                    <option value={7}>07 - ЕЛЕКТРОНСКИ СИСТЕМ И АВТОМАТСКА ОБРАБОТКА НА ПОДАТОЦИ (АОП)</option>
                    <option value={8}>08 - ПОСЕБЕН ДЕЛ</option>
                </Select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel htmlFor="grouped-native-select">Под-Категорија</InputLabel>
                <Select native defaultValue="" id="grouped-native-select">
                <option aria-label="None" value="" />
                <optgroup label="Category 1">
                    <option value={1}>Option 1</option>
                    <option value={2}>Option 2</option>
                </optgroup>
                <optgroup label="Category 2">
                    <option value={3}>Option 3</option>
                    <option value={4}>Option 4</option>
                </optgroup>
                </Select>
            </FormControl>
            <br/>
            <RaisedButton 
                label="Назад"
                primary={false}
                style={styles.button}
            />
            <RaisedButton
                label="Додај"
                id="button-continue" 
                primary={true}
                style={styles.button}
            />
    </React.Fragment>
    </MuiThemeProvider>
    </div>
  );
}

const styles ={
    button: {
        margin: 15,
    },
    banner:{
        backgroundColor: "#3257a8"
    },
    subheading: {
        color: "rgb(136, 136, 136)",
        fontWeight: "600"
    },
}
