import React from 'react';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
    width: 650,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function SimpleSelect() {
  const classes = useStyles();
  const [firma, setfirma] = React.useState('');
  const [cat, setcat] = React.useState('');
  const [subcat, setsubcat] = React.useState('');

  const handleChange = (event) => {
    setfirma(event.target.value);
    setcat(event.target.value);
    setsubcat(event.target.value);
  };

  return (
    <div>
    <MuiThemeProvider>
    <React.Fragment> 
            <AppBar 
            title="Era Consulting"
            style={styles.banner}
            id="banner"
            />
            <h2 
            style={styles.subheading}
            >Додај</h2>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Фирма</InputLabel>
                <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={firma}
                onChange={handleChange}
                >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
                </Select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Категорија</InputLabel>
                <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={cat}
                onChange={handleChange}
                >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>30</MenuItem>
                </Select>
            </FormControl>
            <br/>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Под-Категорија</InputLabel>
                <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={subcat}
                onChange={handleChange}
                >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>40</MenuItem>
                </Select>
            </FormControl>
    </React.Fragment>
    </MuiThemeProvider>
    </div>
  );
}

const styles ={
    banner:{
        backgroundColor: "#3257a8"
    },
    subheading: {
        color: "rgb(136, 136, 136)",
        fontWeight: "600"
    },
}
